import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';
import './Attractions.css'
import slideshow1 from '../images/21c.png'
import slideshow2 from '../images/paul-brown.jpg'
import slideshow3 from '../images/otr.jpg'

const linkStyle = {
  textDecoration: 'none',
  color: 'white',
}


const Attraction = (props) => {
  // display each attraction in a card or some styling, similar to the pet store 
  return (

    <div className='container'>
      <div className='card' style={{ width: '40rem' }}>
        <img className='card-img-top' src={props.attraction.imageUrl} alt="card-image" style={{ width: '100%'}} />
        <div className="card-body center">
          <h1 className='card-title'>
            {props.attraction.name}
          </h1>
          <div className="card-text">
            <p>
              {props.attraction.description}
              <br />
              {props.attraction.city}
              ,
              {props.attraction.state}
              <br />
              {props.attraction.zipcode}
            </p>
          </div>

        </div>
      </div>

    </div>


  )
}

export default class Attractions extends Component {
  constructor(props) {
    super(props)
    this.state = {
      attractions: [],
      loading: true
    }
  }
  componentDidMount() {
    axios.get('http://localhost:5000/' + "Attractions/")
      .then(response => {
        this.setState({
          attractions: response.data,
          loading: false
        })
        console.log('heres the list of attractions')
      })
      .catch((error) => {
        console.log(error)
      })
  }

  attractionList() {
    return this.state.attractions.map((currentAttraction) => {
      return <Attraction attraction={currentAttraction} key={currentAttraction._id} />
    })
  }

  render() {
    return (
      this.state.loading === false ? (
        <div>
          <div id='visit-header'>
            <h1>Visit Cincinnati</h1>
            <br />
            <h5 >Are you on a family vacation? Maybe you just need time to get away. Our city has options for everyone! How would YOU like to plan your trip?</h5>
          </div>

          <br />

          
          <div className="food-image">
        
             <h3>Food & Drinks</h3>
             <Link to='/food' style={linkStyle}><img src="https://ohiotimesnewsimages.s3.amazonaws.com/wp-content/uploads/2021/10/22081827/98040cb9-3e18-45cf-8f0c-0f6f99d8aa4b-Burger_2.jpg" style={{width:'50%'}} alt="food-image" /></Link>
          </div>

          <div className="arts-image">
            <h3>Arts & Music</h3>
            <Link to='/arts' style={linkStyle}><img src="https://cdn2.cincinnatimagazine.com/wp-content/uploads/sites/5/2021/02/DSC_5152-190-scaled.jpg" style={{width:'50%'}} alt="" /></Link>
          </div>
          
          
            <h3 className="sports-image"><Link to='/sports' style={linkStyle}>Sports</Link></h3>
         
            
            
            <h3><Link to='/themeparks' style={linkStyle}>Amusements & Theme Parks</Link></h3>
            <h3><Link to='/nightlife' style={linkStyle}>Night Life</Link></h3>
          

          <br />

          {this.attractionList()}
        </div>


      ) : (
        <div className="">loading....</div>
      )
    )
  }
}
